```javascript
const { Client, SlashCommandBuilder, SlashCommandManager, Actions, Interaction, GatewayIntentName, ComponentType, ComponentData, ComponentOption, Events, GatewayIntents } = require('discord.js');
const { v4: uuidv } = require('uuid');

const client = new Client({ intents: [GatewayIntentName.Guilds, GatewayIntentName.GuildMembers, GatewayIntentName.GuildVoiceState,...GatewayIntents.FLAGS_FOR_OTHER_INTENTS()] });

client.commands = new SlashCommandManager();

async function banUserInteraction(interaction) {
    const { member, guild } = interaction.options.userMention?.member? { member, guild: interaction.guild } : undefined;

    // Check if user has permission
    if (!guild?.members.hasPermission(interaction.user, 'BAN_MEMBERS') && interaction.user.id!== guild.owner.id) {
        return interaction.reply({
            content: 'You do not have permission to ban members.',
            ephemeral: true,
        });
    }

    // Check if user is mentioned
    if (!member) {
        return interaction.reply({
            content: 'Please mention a member to ban.',
            ephemeral: true,
        });
    }

    const reason = interaction.options.getString('reason')?? 'Not specified';

    try {
        await interaction.guild.members.remove(member);
        await interaction.reply({
            content: `${member.user.tag} has been banned. Reason: ${reason}`,
            ephemeral: true,
        });
    } catch (error) {
        interaction.reply({
            content: 'An error occurred while banning the user.',
            ephemeral: true,
        });
    }
}

client.slashCommands.set({
    name: 'ban',
    description: 'Bans a member from the server.',
    options: new SlashCommandBuilder()
       .addUserOption((option) =>
            option.setName('reason')
               .setDescription('Reason for banning (optional)')
        )
       .addIntegerOption((option) => option.setName('maxAttempts').setDescription('Not used in this example'))
       .build())
   .setExecute(interaction => banUserInteraction(interaction))
   .addCondition((condition) => condition.customID === undefined || condition.customID === interaction.id)
   .addIntents(Events.GuildMemberRemoveIntent, { priority: 100 }),
});

client.on('ready', () => {
    console.log(`Logged in as ${client.user.tag}`);
});

client.on('uncaughtException', (err) => {
    console.error(err);
});

client.login(process.env.DISCORD_BOT_TOKEN);

module.exports = client;
```

