```cpp
#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string file_name, target_word;
    int count = 0;

    // Input file name and target word from user
    std::cout << "Enter the file name: ";
    std::cin >> file_name;
    std::cout << "Enter the word to count: ";
    std::cin >> target_word;

    std::ifstream file(file_name);
    if (!file.is_open()) {
        std::cerr << "Error: Unable to open file." << std::endl;
        return 1;
    }

    std::string line;
    // Read file line by line
    while (std::getline(file, line)) {
        // Convert line and target_word to lower case for case-insensitive comparison
        std::string search_line(line);
        std::transform(search_line.begin(), search_line.end(), search_line.begin(), ::tolower);
        std::string lower_target_word(target_word.begin(), target_word.end(), ::tolower);
        if (search_line.find(lower_target_word)!= std::string::npos) {
            ++count;
        }
    }

    std::cout << "The word '" << target_word << "' appears " << count << " times in the file." << std::endl;

    file.close();
    return 0;
}
```

