    const modal = document.getElementById('modal');
    const chatHistory = document.getElementById('chat-history');

    let humanHistory = [];
    let botHistory = [];

    function showMessage(messageType, text) {
        const newMessage = document.createElement('div');
        newMessage.classList.add(messageType ==='message-bot'?'message-bot' :'message-human');
        newMessage.textContent = text;
        newMessage.classList.add('message');

        if (!humanHistory.length || humanHistory.length < 2) {
            botHistory.push(newMessage);
        } else if (!botHistory.length || botHistory.length < 2) {
            humanHistory.push(newMessage);
        } else {
            const lastHuman = humanHistory[humanHistory.length - 1];
            const lastBot = botHistory[botHistory.length - 1];

            if (messageType ==='message-bot') {
                lastHuman.textContent = `${lastHuman.textContent} ${lastBot.textContent}`;
                humanHistory.push(newMessage);
            } else {
                lastBot.textContent = `${lastBot.textContent} ${text}`;
                botHistory.push(newMessage);
            }
        }

        chatHistory.appendChild(newMessage);
        chatHistory.scrollTop = chatHistory.scrollHeight;
    }

    const showModal = () => {
        modal.style.display = 'block';
    };

    const closeModal = () => {
        modal.style.display = 'none';
    };

    const showMessages = () => {
        humanHistory.forEach(index => {
