#include <algorithm>
#include <regex>

std::string markdownToHtml(std::string markdownText) {
    std::string htmlText = markdownText;

    // Replace <strong> tags with <strong> tags (using std::regex)
    std::regex strongRegex("<strong>(.+?)</strong>");
    std::smatch match;
    while (std::regex_search(markdownText.begin(), markdownText.end(), strongRegex, match)) {
        htmlText.replace(match.str().begin(), match.str().end(), "<strong>" + match.str().substr(1) + "</strong>");
    }

    // Replace <em> tags with <em> tags (using std::regex)
    strongRegex = std::regex("<em>(.+?)</em>");
    match.clear();
    while (std::regex_search(markdownText.begin(), markdownText.end(), strongRegex, match)) {
        htmlText.replace(match.str().begin(), match.str().end(), "<em>" + match.str().substr(1) + "</em>");
    }

    // Replace <strong> tags with <i> tags (using std::regex)
    strongRegex = std::regex("<strong>(.+?)</strong>");
    match.clear();
    while (std::regex_search(markdownText.begin(), markdownText.end(), strongRegex, match)) {
        htmlText.replace(match.str().begin(), match.str().end(), "<i>" + match.str().substr(1) + "</i>");
    }

    // Replace <em> tags with <i> tags (using std::regex)
