// Function to count the occurrences of a word in a string
int countWordOccurrences(const std::string &str, const std::string &word) {
    int count = 0;
    size_t pos = 0;

    while ((pos = str.find(word, pos))!= std::string::npos) {
        ++count;
        pos += word.length();
    }

    return count;
}

